import React, { useState } from "react";
import "./RoomDetail.css";
import { useParams, useRouteMatch, useHistory } from "react-router-dom";
import p9 from "../imge/img-9.jpeg";
import MapProductDetail from "./MapProductDetail";
import firebase from "firebase";
import styled from "styled-components";
import { BsFillHeartFill } from "react-icons/bs";
import DateRangePickerExample from "./DateRangePickExample";
import CountNumber from "./ CountNumber";
import { Link } from "react-router-dom";

const Like = styled.button`
  border: none;
  padding-right: 10px;
  padding-left: 10px;
  padding-top: 8px;
  border-radius: 50%;
  font-size: 20px;
  margin-left: 20px;
  cursor: pointer;
`;

const CuntDate = styled.div`
  margin-top: 20px;
`;

const InstantBook = styled.button`
  background-color: #00bfac;
  /* justify-content: center; */
  position: relative;
  border: none;
  width: 80%;
  color: white;
  border-radius: 5px;
  font-size: 15px;
  font-weight: 700;
  height: 50px;
  margin: 20px 30px;
  cursor: pointer;
  &:hover {
    color: #00bfac;
    background: rgb(221, 221, 221);
  }
`;

const SubTitle = styled.div`
  margin-bottom: 10px;
`;

function RoomDetail() {
  const { itemID } = useParams();
  const [item, setItem] = React.useState({});
  const [startDate, setStartDate] = React.useState();
  const [endDate, setEndDate] = React.useState();
  const [count, setCount] = React.useState(0);
  const [comment, setComment] = React.useState([]);
  const { url } = useRouteMatch();
  const history = useHistory();

  // const [count, setCount] = useState(0);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .doc(itemID)
      .onSnapshot((docSnapshot) => {
        const data = docSnapshot.data();
        setItem(data);
      });
    // .get()
    // .then((docSnapshot) => {
    //   const data = docSnapshot.data();
    //   setItem(data);
    // });
  }, []);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("comment")
      .where("itemID", "==", itemID)
      .get()
      .then((snapshot) => {
        console.log(typeof snapshot);
        snapshot.forEach((doc) => {
          const data = doc.data();
          console.log(data);
          setComment((comment) => [...comment, data]);
        });
      });
  }, []);

  function toggleCollection() {
    const uid = firebase.auth().currentUser.uid;
    if (isCollected) {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
          collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
        });
    } else {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
