import React from "react";
import Popup from "reactjs-popup";
import "./PopupButtonSignUp.css";
import Logo from "../imge/logo.png";
import firebase from "../utils/firebase";
import { useHistory } from "react-router-dom";

function PopupButtonSignUp() {
  const history = useHistory();
  const [email, setEmail] = React.useState("");
  const [password, setPsaaword] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  function onSubmit() {
    firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => {
        history.push("/");
      })
      .catch((error) => {
        switch (error.code) {
          case "auth/email-already-in-use":
            setError("This Email has already been used");
            break;
          default:
        }
      });
  }

  return (
    <Popup
      trigger={
        <div
          type="button"
          name="btn-Native-signup"
          className="btn-nativ-signup"
        >
          Sign up
        </div>
      }
      modal
      nested
    >
      {(close) => (
        <div className="modal">
          <button className="close" onClick={close}>
            X
          </button>
          <div className="header">
            Welcome to{" "}
            <img className="signup__logo" src={Logo} alt="signup__logo" />
          </div>
          <div className="header__2">
            Search, discover and book
            <br />
            Everywhere you want to camp.
          </div>
          <div className="signup-areas">
            {" "}
            <form>
              <div
                type="button"
                name="btn-google-login"
                className="btn-google-login"
              >
                Google login
              </div>
              <div
                type="button"
                name="btn-facebook-login"
                className="btn-facebook-login"
              >
                Facebook login
              </div>
              <form>
                <div className="signup-name">
                  <input
                    className="signup-input"
                    name="name"
                    type="name"
                    placeholder="name"
                  ></input>
                </div>
                <div className="signup-email">
                  <input
                    className="signup-input"
                    name="email"
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  ></input>
                </div>
                <div className="signup-password">
                  <input
                    className="signup-input"
                    naem="password"
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPsaaword(e.target.value)}
                  ></input>
                </div>
                {errorMessage && <div>{errorMessage}</div>}
                <div
                  type="button"
                  name="btn-Native-signup"
                  className="btn-nativ-signup"
                  onClick={() => {
                    console.log("Sign up");
                    onSubmit();
                  }}
                >
                  Sign up
                </div>
              </form>
            </form>
          </div>
        </div>
      )}
    </Popup>
  );
}

export default PopupButtonSignUp;
