import React, { useRef } from "react";
import firebase from "firebase";
import { useHistory } from "react-router-dom";
import styled from "styled-components";

function Backstage() {
  //   const nameRef = useRef();

  const [name, setName] = React.useState("");
  const [locationDetail, setLocationDetail] = React.useState("");
  const [guests, setGuests] = React.useState("");
  const [stars, setStars] = React.useState("");
  const [price, setPrice] = React.useState("");
  const [product, setProduct] = React.useState([]);
  const [files, setFile] = React.useState(null);
  const history = useHistory();

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .get()
      .then((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((doc) => {
          return doc.data();
        });
        setProduct(data);
      });
  }, []);

  function onSubmit() {
    const documentRef = firebase.firestore().collection("product").doc();
    documentRef
      .set({
        name,
        locationDetail,
        guests,
        stars,
        price,
      })
      .then(() => {
        history.push("/");
      });
  }

  //   const Rooms = styled.div`
  //     margin-left: 50px;
  //   `;

  //   const Title = styled.div`
  //     font-size: 20px;
  //     margin-top: 30px;
  //   `;

  //   const Camping = styled.div`
  //     /* display: inline-block; */
  //   `;

  //   const Input = styled.input`
  //     display: flex;
  //     margin-top: 10px;
  //     width: 20%;
  //     height: 30px;
  //     margin-bottom: 10px;
  //   `;

  //   const Button = styled.button`
  //     width: 21%;
  //     height: 30px;
  //     margin-bottom: 50px;
  //   `;

  return (
    <div>
      <div>上架露營地</div>
      <div>
        {/* <img
          className="rooms__backstage__back__img"
          src="https://react.semant-ui.com/images/wireframe/image.png"
          alt="back__img"
        />  */}

        <input
          type="file"
          accept="image/png"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <input
          placeholder="露營地名稱"
          //   ref={nameRef}
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          placeholder="露營地地點"
          value={locationDetail}
          onChange={(e) => setLocationDetail(e.target.value)}
        />
        <input
          placeholder="露營地人數"
          value={guests}
          onChange={(e) => setGuests(e.target.value)}
        />
        <input
          placeholder="露營地評分"
          value={stars}
          onChange={(e) => setStars(e.target.value)}
        />
        <input
          placeholder="露營地價格"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <button onClick={() => onSubmit()}>送出</button>
      </div>
    </div>
  );
}

export default Backstage;
