import React from "react";
import GoogleMap from "./GoogleMap";
import { Link } from "react-router-dom";
import firebase from "firebase";
import styled from "styled-components";
import { BsFillHeartFill } from "react-icons/bs";
import { useHistory } from "react-router";

const Like = styled.button`
  border: none;
  padding-right: 10px;
  padding-left: 10px;
  padding-top: 8px;
  border-radius: 50%;
  font-size: 20px;
  /* margin-left: -20px; */
  cursor: pointer;
`;

const TagArea = styled.div`
  border-bottom: 2px solid rgb(212, 212, 212);
  padding: 20px;
  margin: 16px 0px 0px 0px;
`;

const TagBtn = styled.button`
  border-radius: 5px;
  background-color: white;
  margin-right: 13px;
  margin-top: -5px;
  padding: 7px 20px;
  color: #00bfac;
  border: 2px #00bfac solid;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
`;

const Wrapper = styled.div`
  display: flex;
  @media screen and (max-width: 1087px) {
    display: block;
  }
`;

const Map = styled.div`
  position: sticky;
  top: 0px;
  @media screen and (max-width: 1087px) {
    display: none;
  }
`;

const Items = styled.div`
  margin: 0px;
  width: 50%;
  @media screen and (max-width: 1087px) {
    width: 100%;
  }
`;

const MapWrapper = styled.div`
  width: 50%;
`;

const Item = styled.div`
  display: flex;
  border-bottom: 1px solid rgb(212, 212, 212);
  margin-bottom: 8px;
  padding: 10px;
  width: 100%;
`;

const ItemImage = styled.img`
  width: 300px;
  overflow: hidden;
  border-radius: 20px;
`;

const Box = styled.div`
  display: inline-block;
  vertical-align: top;
  padding: 20px;
  font-family: "-apple-system", "BlinkMacSystemFont", "Segoe UI",
    "Roboto, Oxygen", "Ubuntu, Cantarell", "Open Sans", "Helvetica Neue",
    "sans-serif";
`;

const ItemLocation = styled.div`
  color: rgb(151, 145, 145);
  padding-bottom: 8px;
`;

const ItemName = styled.div`
  font-size: 24px;
  font-family: "Montserrat", sans-serif;
  font-weight: 500;
  width: 100%;
`;

const Hr = styled.hr`
  width: 80px;
  margin: 15px 0;
`;

const ItemGuests = styled.div`
  font-size: 18px;
  color: gray;
`;

const ItemStars = styled.div`
  color: black;
  padding: 10px 0;
  font-weight: 500;
`;

const ItemComment = styled.span`
  color: gray;
  font-weight: 400;
`;

const ItemPrice = styled.div`
  font-weight: 600;
  font-size: 26px;
  margin-top: 20px;
`;

const ItemTags = styled.div`
  font-size: 15px;
  margin-top: 10px;
  margin-right: 10px;
  display: inline-block;
  background: #fbba05;
  color: white;
  padding: 5px 5px;
  border: 1px gray;
  border-radius: 3px;
  text-align: center;
  cursor: pointer;
  font-weight: bold;
`;

function Rooms() {
  const [rooms, setRooms] = React.useState([]);
  const [tag, setTag] = React.useState("All");
  const history = useHistory();
  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .onSnapshot((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((docSnapshot) => {
          const id = docSnapshot.id;
          return { ...docSnapshot.data(), id };
        });
        setRooms(data);
        console.log(data);
      });
  }, []);

  function renderRoom(item) {
    const uid = firebase.auth().currentUser?.uid;
    function toggleCollection() {
      if (uid) {
        if (isCollected) {
          firebase
            .firestore()
            .collection("product")
            .doc(item.id)
            .update({
              collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
            });
        } else {
          firebase
            .firestore()
            .collection("product")
            .doc(item.id)
            .update({
              collectionBy: firebase.firestore.FieldValue.arrayUnion(uid),
            });
        }
      } else {
        history.push("/log-in");
      }
    }

    const isCollected = item.collectionBy?.includes(
      firebase.auth().currentUser?.uid
    );

    return (
      <Item>
        <Link to={`/products/${item.id}`}>
          <ItemImage src={item.imageURL} />
        </Link>
        <Box>
          <ItemLocation>⌲{item.address}</ItemLocation>
          <ItemName>{item.name}</ItemName>
          <Hr />
          <ItemGuests>{item.guests} guests</ItemGuests>
          <ItemStars>
            ★{item.stars}
            <ItemComment> ({item.commentCount} 評論)</ItemComment>
          </ItemStars>
          <Like
            onClick={toggleCollection}
            style={isCollected ? { color: "red" } : { color: "grey" }}
          >
            <BsFillHeartFill />
          </Like>

          {/* <div className="rooms__item__like">like:{props.like}</div> */}
          <ItemPrice>NT.{item.price}/一晚</ItemPrice>
          <ItemTags>#{item.discountTag}</ItemTags>
          <ItemTags>{item.otherTag}</ItemTags>
        </Box>
      </Item>
    );
  }

  return (
    <div className="rooms">
      {}
      <div className="rooms__condition__container">
        <div className="rooms__condition__wrapper">
          <TagArea>
            <TagBtn
              onClick={() => setTag("All")}
              style={
                tag === "All"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              All
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Seaview")}
              style={
                tag === "Seaview"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Seaview
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Mountainview")}
              style={
                tag === "Mountainview"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Mountainview
            </TagBtn>
            <TagBtn
              onClick={() => setTag("SwimmingPool")}
              style={
                tag === "SwimmingPool"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Swimming Pool
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Barbecue")}
              style={
                tag === "Barbecue"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Barbecue
            </TagBtn>
          </TagArea>
        </div>
      </div>
      <div className="rooms__container">
        <Wrapper>
          <Items>
            {rooms
              .filter((item) => {
                if (tag === item.topicTag) {
                  return item;
                } else if (tag === "All") {
                  return rooms;
                }
              })

              // (tag ? item.topicTag === tag : rooms))
              .map((item) => renderRoom(item))}
          </Items>
          <MapWrapper>
            <Map>
              <GoogleMap tag={tag} />
            </Map>
          </MapWrapper>
        </Wrapper>
      </div>
    </div>
  );
}

export default Rooms;
