import React from "react";

import "./Rooms.css";
import { Link } from "react-router-dom";
import firebase from "firebase";
import styled from "styled-components";

const Item = styled.div`
  display: flex;
  border-bottom: 1px solid rgb(212, 212, 212);
  margin-bottom: 8px;
  padding: 10px;
  width: 100%;
`;
const ItemImage = styled.img`
  width: 300px;
  overflow: hidden;
  border-radius: 20px;
`;

const Box = styled.div`
  display: inline-block;
  vertical-align: top;
  padding: 20px;
  font-family: "-apple-system", "BlinkMacSystemFont", "Segoe UI",
    "Roboto, Oxygen", "Ubuntu, Cantarell", "Open Sans", "Helvetica Neue",
    "sans-serif";
`;

const ItemLocation = styled.div`
  color: rgb(151, 145, 145);
  padding-bottom: 8px;
`;

const ItemName = styled.div`
  font-size: 24px;
  font-family: "Montserrat", sans-serif;
  font-weight: 500;
  width: 100%;
`;

function MyWishList() {
  const [rooms, setRooms] = React.useState([]);
  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .where("collectionBy", "array-contains", firebase.auth().currentUser.uid)
      .onSnapshot((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((docSnapshot) => {
          const id = docSnapshot.id;
          return { ...docSnapshot.data(), id };
        });
        setRooms(data);
        console.log(data);
      });
  }, []);

  function renderRoom(item) {
    return (
      <Item>
        <Link to={`/products/${item.id}`}>
          <ItemImage src={item.imageURL} />
        </Link>
        <Box>
          <ItemLocation>⌲{item.address}</ItemLocation>
          <ItemName>{item.name}</ItemName>
          <hr />
          <div className="rooms_item__guest">{item.guests} guests</div>
          <div className="rooms__item__stars">
            ★{item.stars}
            <span className="visits"> (5 comments)</span>
          </div>
          <div className="rooms__item__price">{item.price}/night</div>
          <div className="rooms__item__heart"></div>
        </Box>
      </Item>
    );
  }

  return (
    <div className="rooms">
      <div className="rooms__container">
        <div className="rooms__wrapper">
          <div className="rooms__items">
            <div className="title">Wishlists</div>
            {rooms.map((item) => renderRoom(item))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MyWishList;
