import React from "react";
import { zhTW } from "date-fns/locale";
import { DateRangePicker } from "react-nice-dates";
import "react-nice-dates/build/style.css";
import { getDay, getDate, isSameDay } from "date-fns";
import styled from "styled-components";
import "./DateRangPickExample.css";
import firebase from "firebase";
import { useParams } from "react-router";

const Input = styled.input`
  width: 139px;
  height: 55px;
  cursor: pointer;
  border: 1px solid rgb(221, 221, 221);
`;

const Range = styled.div`
  margin: 0px 20px;
  padding: 10px;
`;

function DateRangePickerExample(props) {
  const [successfullyOrder, setSuccessfullyOrder] = React.useState([]);
  const { itemID } = useParams();

  const banDateArr = [];

  const startDate = successfullyOrder.map((date) => {
    banDateArr.push(new Date(date.startDate.seconds * 1000));
    return new Date(date.startDate.seconds * 1000);
  });
  console.log(startDate);

  const endDate = successfullyOrder.map((date) => {
    return new Date(date.endDate.seconds * 1000);
  });
  console.log(endDate[0]);

  const modifiers = {
    // Disables Saturdays
    disabled: (date) =>
      startDate.filter((startDate) => {
        return getDate(date) === getDate(startDate[0]);
      }),
    // getDate(date) === getDate(startDate[0]) && getDate(endDate[0]),
  };

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("successfullyOrder")
      .where("itemID", "==", itemID)
      .onSnapshot((querySnapshot) => {
        const data = [];
        querySnapshot.forEach((doc) => {
          data.push(doc.data());
        });
        setSuccessfullyOrder(data);
        console.log(data);
      });
  }, []);

  return (
    <DateRangePicker
      startDate={props.startDate}
      endDate={props.endDate}
      onStartDateChange={props.setStartDate}
      onEndDateChange={props.setEndDate}
      minimumDate={new Date()}
      minimumLength={1}
      format="dd MMM yyyy"
      locale={zhTW}
      modifiers={modifiers}
      // modifiersClassNames={modifiersClassNames}
    >
      {({ startDateInputProps, endDateInputProps, focus }) => (
        <Range>
          <Input {...startDateInputProps} placeholder="入住" />
          <Input {...endDateInputProps} placeholder="退房" />
        </Range>
      )}
    </DateRangePicker>
  );
}

export default DateRangePickerExample;
