import React from "react";
import "../App.css";
import video from "../video/video-3.mp4";
import "./HeroSection.css";
import DateRangePickerExample from "./DateRangePickExample";
import CountNum from "./CountNum";
import styled from "styled-components";

const Video = styled.video`
  object-fit: cover;
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: -1;
`;

function HeroSection() {
  return (
    <div className="hero-container">
      <Video src={video} autoPlay loop muted></Video>
      <h1>START CAMPING</h1>
      <p>Find your best campground</p>

      <div className="search-areas">
        <form>
          {/* <h5>WHERE TO?</h5> */}
          <div className="search-location">
            <h5 className="search-word">WHERE TO?</h5>
            <input
              className="search-input"
              name="location"
              type="location"
              placeholder="Try somewhere"
            />
          </div>
          {/* <h5>DATES</h5> */}
          <div className="search-date">
            <h5 className="search-word">DATES</h5>
            <DateRangePickerExample />
            {/* <input
              className="search-input"
              name="date"
              type="date"
              placeholder="Enter Dates"
            /> */}
          </div>
          {/* <h5>GUESTS</h5> */}
          <div className="search-guests">
            <h5 className="search-word">GUESTS</h5>
            <CountNum />
          </div>
          <div className="btn-search" type="button" name="search-button">
            Search
          </div>
        </form>
      </div>
    </div>
  );
}

export default HeroSection;
