function RoomDetail() {
  const { itemID } = useParams();
  const [item, setItem] = React.useState({});
  const [startDate, setStartDate] = React.useState("");
  const [endDate, setEndDate] = React.useState("");
  const [count, setCount] = React.useState(0);
  const [comment, setComment] = React.useState([]);
  const { url } = useRouteMatch();
  const history = useHistory();

  // const [count, setCount] = useState(0);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .doc(itemID)
      .onSnapshot((docSnapshot) => {
        const data = docSnapshot.data();
        setItem(data);
      });
    // .get()
    // .then((docSnapshot) => {
    //   const data = docSnapshot.data();
    //   setItem(data);
    // });
  }, []);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("comment")
      .where("itemID", "==", itemID)
      .get()
      .then((snapshot) => {
        console.log(typeof snapshot);
        snapshot.forEach((doc) => {
          const data = doc.data();
          console.log(data);
          setComment((comment) => [...comment, data]);
        });
      });
  }, []);

  function toggleCollection() {
    const uid = firebase.auth().currentUser.uid;
    if (isCollected) {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
          collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
        });
    } else {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
          collectionBy: firebase.firestore.FieldValue.arrayUnion(uid),
        });
    }
  }

  const isCollected = item.collectionBy?.includes(
    firebase.auth().currentUser.uid
  );

  function onSubmit() {
    // const documentRef = firebase.firestore().collection("order").doc();
    const orderID = firebase.firestore().collection("order").doc().id;
    if (startDate === "") {
      alert("請選擇入住時間");
      Swal.fire("Any fool can use a computer");
    } else if (endDate === "") {
      alert("請輸入退房時間");
    } else if (count === 0) {
      alert("請選擇入住人數");
    } else {
      firebase
        .firestore()
        .collection("order")
        .doc(orderID)
        .set({
          startDate,
          endDate,
          uid: firebase.auth().currentUser.uid,
          id: itemID,
          count,
        })
        .then(() => {
          history.push(`${url}/order/${orderID}`);
        });
    }
  }

  return (
    <div className="rooms__detail__container">
      <div className="rooms__detail__wrapper">
        <div className="rooms__detail__introduce">
          <RoomsDetailBox>
            <RoomsDetailName>{item.name}</RoomsDetailName>
            <div className="rooms__name__info">
              <RoomsDetailStars>
                ★{item.stars} ({comment.length} comments)
              </RoomsDetailStars>
              <RoomsDetailAddress>⌲{item.address}</RoomsDetailAddress>
              <Like
                onClick={toggleCollection}
                style={isCollected ? { color: "red" } : { color: "grey" }}
              >
                <BsFillHeartFill />
              </Like>
            </div>
            <div className="rooms__detail__carousel">
              <RoomsDetailImage src={item.imageURL} />
            </div>
            <div className="rooms__owner">
              <RoomsOwnerName>Hosted by Victor Chen</RoomsOwnerName>
              <div className="rooms__stick">
                <div className="rooms__owner__detail">
                  <RoomsOwnerIntroduce>{item.comment}</RoomsOwnerIntroduce>
                </div>
                <div className="rooms__detail__check">
                  <RoomsCheckBox>
                    <RoomsBoxFirstRow>
                      <RoomsBoxPrice>NT.{item.price}/晚</RoomsBoxPrice>
                      <RoomsBoxStarts>
                        ★{item.stars} ({comment.length} comments)
                      </RoomsBoxStarts>
                    </RoomsBoxFirstRow>
                    <div className="rooms__box__date">
                      {/* <SubTitle>入住時間</SubTitle> */}
                      <DateRangePickerExample
                        startDate={startDate}
                        setStartDate={setStartDate}
                        endDate={endDate}
                        setEndDate={setEndDate}
                      />
                      {/* <input className="checkin" placeholder="Check in" />
                      <input className="checkout" placeholder="Check out" /> */}
                    </div>
                    {/* <SubTitle>入住人數</SubTitle> */}
                    <CuntDate>
                      <CountNumber count={count} setCount={setCount} />
                    </CuntDate>
                    <InstantBook
                      // history.push(`${url}/order`)
                      onClick={() => onSubmit()}
                    >
                      Instant book
                    </InstantBook>
                  </RoomsCheckBox>
                </div>
              </div>
            </div>
          </RoomsDetailBox>
          <div className="Equipment">
            <div className="Campsite__area">
              <div className="title">Campsite area</div>
              <div className="area__point">Park at listing</div>
              <div className="area__point">Canvas tent</div>
            </div>
            <div className="Essentials">
              <div className="title">Essentials</div>

              <div className="essentials__point">Pets allowed</div>
              <div className="essentials__point">Campfires allowed</div>
            </div>
            <div className="Amenities">
              <div className="title">Amenities</div>

              <div className="amenities__point">No wifi</div>
              <div className="amenities__point">Pack out</div>
              <div className="amenities__point">Showers</div>
              <div className="amenities__point">Potable water</div>
            </div>
          </div>
          <div className="Details">
            <div className="details__title">Details</div>
            <div className="details__info">
              <div className="details__points">Check in:After 3PM</div>
              <div className="details__points">Check out:Before 12PM</div>
              <div className="details__points">On arrival: Meet and greet</div>
            </div>
          </div>
          <div className="Activities">
            <div className="activities__title">Activities</div>
            <div className="activities__sub__title">
              Offered on the Host's property or nearby.
            </div>
            <div className="activities__box">
              <div className="activities__points">
                <div className="activities__points__name">Biking</div>
              </div>
              <div className="activities__points">
                <div className="activities__points__name">Boating</div>
              </div>
              <div className="activities__points">
                <div className="activities__points__name">Hiking</div>
              </div>
              <div className="activities__points">
                <div className="activities__points__name">Padding</div>
              </div>
              <div className="activities__points">
                <div className="activities__points__name">Swimming</div>
              </div>
            </div>
          </div>
          <div className="Reviews">
            <div className="reviews__title">{comment.length} Reviews</div>
            {comment.map((data, index) => {
              return (
                <div className="reviews__box" key={index}>
                  <img
                    className="reviewer__img"
                    alt="reviewer"
                    src={data.author.photoURL}
                  />
                  <div className="reviewer__info">
                    <div className="reviewer__stars">★{data.rating}</div>
                    <div className="reviewer__name">{data.author.email}</div>
                    <div className="reviewer__comment">{data.review}</div>
                    <div className="reviewer__date">
                      {data.createdAt.toDate().toLocaleDateString()}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="porduct__detail__map">
            <MapProductDetail />
          </div>
        </div>
      </div>
    </div>
  );
}

