import React from "react";

import video from "../video/video-4.mp4";
import "./HeroSectionLogin.css";
import PopupButtonSignUp from "./PopupButtonSignUp";
import firebase from "firebase";
import { useHistory } from "react-router";
import styled from "styled-components";

const Video = styled.video`
  object-fit: cover;
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: -1;
`;

const LogInContainer = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: inset 0 0 0 1000px rgba(0, 0, 0, 0.2);
  object-fit: contain;
`;

function HeroSectionLogin() {
  const history = useHistory();
  const [LoginEmail, setLoginEmail] = React.useState("");
  const [LoginPassword, setLoginPsaaword] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  var fbProvider = new firebase.auth.FacebookAuthProvider();
  const fbLogin = () => {
    firebase
      .auth()
      .signInWithPopup(fbProvider)
      .then((user) => {
        console.log(user);
        // createUserDoc(user, userName);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const googleProvider = new firebase.auth.GoogleAuthProvider();
  const googleLogin = () => {
    firebase
      .auth()
      .signInWithPopup(googleProvider)
      .then((user) => {
        console.log(user);
        history.push("/");
        // createUserDoc(user, userName);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  function onSubmit() {
    firebase
      .auth()
      .signInWithEmailAndPassword(LoginEmail, LoginPassword)
      .then(() => {
        history.push("/");
      })
      .catch((error) => {
        console.log(error);
        switch (error.code) {
          case "auth/invalid-email":
            setErrorMessage(" Incorrect Email format");
            break;
          default:
        }

        switch (error.code) {
          case "auth/wrong-password":
            setErrorMessage(" Wrong password");
            break;
          default:
        }
      });
  }

  return (
    <LogInContainer>
      <Video src={video} autoPlay loop muted></Video>
      <h1>Welcome back!</h1>
      <p>Let's get you intside.</p>
      <div className="login-areas">
        <form>
          <div
            type="button"
            name="btn-google-login"
            className="btn-google-login"
            onClick={googleLogin}
          >
            Google log in
          </div>
          <div
            type="button"
            name="btn-facebook-login"
            className="btn-facebook-login"
            onClick={fbLogin}
          >
            Facebook log in
          </div>
          <form>
            <div className="login-email">
              <input
                className="login-input"
                name="email"
                type="email"
                placeholder="Email"
                value={LoginEmail}
                onChange={(e) => setLoginEmail(e.target.value)}
              ></input>
            </div>
            <div className="login-password">
              <input
                className="login-input"
                naem="password"
                type="password"
                placeholder="Password"
                value={LoginPassword}
                onChange={(e) => setLoginPsaaword(e.target.value)}
              ></input>
            </div>
            {errorMessage && <h6 style={{ color: "white" }}>{errorMessage}</h6>}
            <div
              type="button"
              name="btn-Native-login"
              className="btn-nativ-login"
              onClick={() => {
                setErrorMessage("");
                onSubmit();
              }}
            >
              Log in
            </div>
          </form>
          <PopupButtonSignUp />
        </form>
      </div>
    </LogInContainer>
  );
}

export default HeroSectionLogin;
