import React, { useCallback, useRef, useState, useEffect } from "react";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import logo from "../imge/logo.png";
import styled from "styled-components";
import firebase from "firebase";
import { GiCampingTent } from "react-icons/gi";

const StyledInfoHeader = styled.h2`
  color: #3264a8;
  font-family: sans-serif;
  font-weight: bold;
  text-align: center;
  font-size: 15px;
`;

const StyledInfoRating = styled.h3`
  color: #b31010;
  font-family: sans-serif;
  font-weight: bold;
  text-align: center;
`;

const StyledInfoOpening = styled.h2`
  color: black;
  font-family: sans-serif;
  font-weight: bold;
  text-align: center;
  font-size: 15px;
`;

const StyledLocationBtn = styled.button`
  margin-top: 30px;
  margin-left: calc(50% - 75px);
  width: 150px;
  height: 50px;
  font-size: 20px;
`;

const StyledLoadingContent = styled.div`
  height: 600px;
  width: 100%;
  background: grey;
`;

const libraries = ["places"];
const mapContainerStyle = {
  height: "100vmin",
  width: "100%",
};

export default function App() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyAAuyNVSxdv2VTjUe_N7hyH8drXIoypa_E",
    libraries,
  });

  //   const [currentLocation, setCurrentLocation] = useState({
  //     lat: 25.0361,
  //     lng: 121.5372,
  //   });

  const [selected, setSelected] = useState(null);
  const [nearby, setNearyby] = useState([]);
  const [places, setPlaces] = useState([]);

  // const mapRef = useRef();
  // const onMapLoad = useCallback((map) => {
  //   mapRef.current = map;
  // }, []);

  // useEffect(() => {
  //   setIsFetching(true)
  //   console.log(currentLocation)
  //   fetch(`https://us-central1-uniquefit-william.cloudfunctions.net/getGoogleNearbySearch?lat=${currentLocation.lat}&lng=${currentLocation.lng}`)
  //     .then(res => res.json())
  //     .then(json => setNearyby(json.results))
  // }, []);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .onSnapshot((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((docSnapshot) => {
          const id = docSnapshot.id;
          return { ...docSnapshot.data(), id };
        });
        setPlaces(data);
        console.log(data);
      });
  }, []);

  //   useEffect(() => {
  //     navigator.geolocation.getCurrentPosition(
  //       (position) => {
  //         setCurrentLocation({
  //           lat: position.coords.latitude,
  //           lng: position.coords.longitude,
  //         });
  //         fetch(
  //           `https://us-central1-uniquefit-william.cloudfunctions.net/getGoogleNearbySearch?lat=${position.coords.latitude}&lng=${position.coords.longitude}`
  //         )
  //           .then((res) => res.json())
  //           .then((json) => setNearyby(json.results));
  //         setIsNavigating(false);
  //       },
  //       () => {
  //         alert("Please allow location tracking!");
  //         setIsNavigating(false);
  //       }
  //     );
  //   }, []);

  //   function dragMarker(coords) {
  //     const { latLng } = coords;
  //     const lat = latLng.lat();
  //     const lng = latLng.lng();

  //     setCurrentLocation({
  //       lat: lat,
  //       lng: lng,
  //     });
  //     fetch(
  //       `https://us-central1-uniquefit-william.cloudfunctions.net/getGoogleNearbySearch?lat=${lat}&lng=${lng}`
  //     )
  //       .then((res) => res.json())
  //       .then((json) => setNearyby(json.results));
  //     setIsNavigating(false);
  //   }

  if (loadError) return "Error";
  if (!isLoaded) return "Loading...";

  //   console.log(nearby);
  //   console.log(selected);

  return (
    <div>
      <GoogleMap
        id="map"
        mapContainerStyle={mapContainerStyle}
        zoom={8}
        center={{
          lat: 23.96363430162278,
          lng: 120.97561230851146,
        }}
        clickableIcons={false}
        // onLoad={onMapLoad}
      >
        {/* <Marker
          position={currentLocation}
          draggable={true}
          onDragEnd={(coords) => dragMarker(coords)}
        /> */}
        {places.map((marker) => (
          <Marker
            key={marker.placeId}
            position={{
              lat: marker.latitude.lat,
              lng: marker.latitude.lng,
            }}
            onClick={() => {
              setSelected(marker);
            }}
            icon={{
              url: GiCampingTent,
              origin: new window.google.maps.Point(0, 0),
              anchor: new window.google.maps.Point(20, 20),
              scaledSize: new window.google.maps.Size(40, 40),
            }}
          />
        ))}

        {selected ? (
          <InfoWindow
            position={{
              lat: selected.latitude.lat,
              lng: selected.latitude.lng,
            }}
            onCloseClick={() => {
              setSelected(null);
            }}
          >
            <div>
              <StyledInfoHeader>{selected.name}</StyledInfoHeader>
              <StyledInfoRating>
                {" "}
                ⭐ {selected.stars} 📍 {selected.address}
              </StyledInfoRating>
            </div>
          </InfoWindow>
        ) : null}
      </GoogleMap>
    </div>
  );
}
