import React, { useState, useRef } from "react";
import "../App.css";
import video from "../video/video-3.mp4";
import "./HeroSection.css";
import DateRangePickerExample from "./DateRangePickExample";
import CountNum from "./CountNum";
import styled from "styled-components";
// import { useHistory } from "react-router-dom";

const Video = styled.video`
  object-fit: cover;
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: -1;
`;

const Container = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: inset 0 0 0 1000px rgba(0, 0, 0, 0.2);
  object-fit: contain;
`;

const H1 = styled.h1`
  color: #fff;
  font-size: 100px;
  margin-top: -100px;
`;

function HeroSection() {
  // const [search, setSearch] = useState("");
  // const searchValue = useRef(null);
  // const history = useHistory();

  // const clickSearch = () => {
  //   console.log(searchValue.current.value);
  //   history.push(`/${searchValue.current.value}`);
  // };

  return (
    <Container>
      <Video src={video} autoPlay loop muted></Video>
      <H1>START CAMPING</H1>
      <p>Find your best campground</p>

      <div className="search-areas">
        <form>
          <div className="search-location">
            <h5 className="search-word">WHERE TO?</h5>
            <input
              className="search-input"
              name="location"
              type="location"
              placeholder="Try somewhere"
              // ref={searchValue}
            />
          </div>

          <div className="search-date">
            <h5 className="search-word">DATES</h5>
            <DateRangePickerExample />
          </div>
          <div className="search-guests">
            <h5 className="search-word">GUESTS</h5>
            <CountNum />
          </div>
          <div
            className="btn-search"
            type="button"
            name="search-button"
            // onClick={() => clickSearch()}
          >
            Search
          </div>
        </form>
      </div>
    </Container>
  );
}

export default HeroSection;
