import React from "react";
import "./CountNum.css";

function CountNum({ count, setCount }) {
  const handleClickAdd = () => {
    setCount(count + 1);
  };

  const handleClickReduce = () => {
    setCount(count - 1);
  };

  return (
    <div className="Guests">
      <div className="handleReduce" onClick={handleClickAdd}>
        -
      </div>
      {/* <span className="Num">{this.state.count}</span> */}
      <div className="handleAdd" onClick={handleClickReduce}>
        +
      </div>
    </div>
  );
}

export default CountNum;
