import React from "react";
import GoogleMap from "./GoogleMap";
import "./Rooms.css";
import { Link } from "react-router-dom";
import firebase from "firebase";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { BsFillHeartFill } from "react-icons/bs";

const Like = styled.button`
  border: none;
  padding-right: 10px;
  padding-left: 10px;
  padding-top: 8px;
  border-radius: 50%;
  font-size: 20px;
  /* margin-left: -20px; */
  cursor: pointer;
`;

const TagArea = styled.div`
  border-bottom: 2px solid rgb(212, 212, 212);
  padding: 20px;
  margin: 16px 0px 0px 0px;
`;

const TagBtn = styled.button`
  border-radius: 5px;
  background-color: white;
  margin-right: 13px;
  margin-top: -5px;
  padding: 7px 20px;
  color: #00bfac;
  border: 2px #00bfac solid;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
`;

const Wrapper = styled.div`
  display: flex;
  @media screen and (max-width: 1087px) {
    .rooms__wrapper {
      display: block;
    }
  }
`;

function Rooms() {
  const [rooms, setRooms] = React.useState([]);
  const [tag, setTag] = React.useState("All");
  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .onSnapshot((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((docSnapshot) => {
          const id = docSnapshot.id;
          return { ...docSnapshot.data(), id };
        });
        setRooms(data);
        console.log(data);
      });
  }, []);

  function renderRoom(item) {
    const uid = firebase.auth().currentUser.uid;
    function toggleCollection() {
      if (isCollected) {
        firebase
          .firestore()
          .collection("product")
          .doc(item.id)
          .update({
            collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
          });
      } else {
        firebase
          .firestore()
          .collection("product")
          .doc(item.id)
          .update({
            collectionBy: firebase.firestore.FieldValue.arrayUnion(uid),
          });
      }
    }

    const isCollected = item.collectionBy?.includes(
      firebase.auth().currentUser.uid
    );

    return (
      <div className="rooms__item">
        <Link className="rooms_item__link" to={`/products/${item.id}`}>
          <img className="rooms__item__img" alt="room" src={item.imageURL} />
        </Link>
        <div className="box">
          <div className="rooms__item__location">⌲{item.address}</div>
          <div className="rooms__item__title">{item.name}</div>
          <hr />
          <div className="rooms_item__guest">{item.guests} guests</div>
          <div className="rooms__item__stars">
            ★{item.stars}
            <span className="visits"> ({item.commentCount} 評論)</span>
          </div>
          <Like
            onClick={toggleCollection}
            style={isCollected ? { color: "red" } : { color: "grey" }}
          >
            <BsFillHeartFill />
          </Like>
          {/* <div className="rooms__item__like">like:{props.like}</div> */}
          <div className="rooms__item__price">NT.{item.price}/一晚</div>
          <div calssName="tag">
            <div className="rooms__item__tag">#{item.discountTag}</div>
            <div className="rooms__item__tag">{item.otherTag}</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="rooms">
      {}
      <div className="rooms__condition__container">
        <div className="rooms__condition__wrapper">
          <TagArea>
            <TagBtn
              onClick={() => setTag("All")}
              style={
                tag === "All"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              All
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Seaview")}
              style={
                tag === "Seaview"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Seaview
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Mountainview")}
              style={
                tag === "Mountainview"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Mountainview
            </TagBtn>
            <TagBtn
              onClick={() => setTag("SwimmingPool")}
              style={
                tag === "SwimmingPool"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Swimming Pool
            </TagBtn>
            <TagBtn
              onClick={() => setTag("Barbecue")}
              style={
                tag === "Barbecue"
                  ? { background: "#00bfac", color: "white" }
                  : { background: "white" }
              }
            >
              Barbecue
            </TagBtn>
          </TagArea>
        </div>
      </div>
      <div className="rooms__container">
        <Wrapper>
          <div className="rooms__items">
            {rooms
              .filter((item) => {
                if (tag === item.topicTag) {
                  return item;
                } else if (tag === "All") {
                  return rooms;
                }
              })

              // (tag ? item.topicTag === tag : rooms))
              .map((item) => renderRoom(item))}
          </div>
          <div className="map__wrapper">
            <div className="map">
              <GoogleMap tag={tag} />
            </div>
          </div>
        </Wrapper>
      </div>
    </div>
  );
}

export default Rooms;
