import React from "react";
import Popup from "reactjs-popup";
import "./PopupButton.css";
import StarRating from "./StarRating";
import firebase from "firebase";
import { useHistory } from "react-router-dom";

function PopupButton(props) {
  console.log(props);
  const [review, setReview] = React.useState();
  const history = useHistory();
  const [selection, setSelection] = React.useState(0);
  const [rating, setRating] = React.useState(0);

  function onSubmit() {
    const commentID = firebase.firestore().collection("comment").doc().id;
    firebase
      .firestore()
      .collection("comment")
      .doc(commentID)
      .set({
        itemID: props.itemID,
        review,
        selection,
        createdAt: firebase.firestore.Timestamp.now(),
        rating,
        author: {
          photoURL: firebase.auth().currentUser.photoURL || "",
          email: firebase.auth().currentUser.email,
          // name: firebase.auth().currentUser.name,
        },
      })
      .then(() => {
        // firebase
        //   .firestore()
        //   .collection("product")
        //   .doc(props.itemID)
        //   .update({
        //     commentCount: props.itemID.commentCount + 1,
        //   })
        //   .then(() => {
        //     window.alert("已成功完成評價");
        //     history.push("/");
        //   });
        console.log(props.itemID.commentCount);
      });
  }

  return (
    <Popup
      trigger={
        <button className="trips__cards__items__button"> Evaluate </button>
      }
      modal
      nested
    >
      {(close) => (
        <div className="modal">
          <button className="close" onClick={close}>
            &times;
          </button>
          <div className="header"> Evaluate </div>
          <div className="content">
            {" "}
            <div className="content-input">
              Stars:
              <StarRating
                setSelection={setSelection}
                setRating={setRating}
                selection={selection}
                rating={rating}
              />
            </div>
            <br />
            <div className="content-input">
              Reviews:
              <input
                className="popupInput"
                value={review}
                onChange={(e) => setReview(e.target.value)}
              ></input>
            </div>
          </div>
          <div className="actions">
            <button className="sendout__button" onClick={() => onSubmit()}>
              Send out
            </button>
          </div>
        </div>
      )}
    </Popup>
  );
}

export default PopupButton;
