import React from "react";
import "./TripsCard.css";
import PopupButton from "./PopupButton";
import firebase from "firebase";
import styled from "styled-components";

const Trips = styled.div`
  margin: 0px auto;
  padding: 30px;
  width: 50%;
`;

const Title = styled.div`
  font-size: 40px;
  text-align: center;
  margin: 20px;
  padding: 10px;
  font-weight: 600;
`;

const TripsCards = styled.div`
  margin: 5px 150px 10px 30px;
  border-radius: 12px;
  box-shadow: rgb(0 0 0 / 12%) 0px 6px 16px;
  border-style: solid;
  border: 1px solid rgb(221, 221, 221);
  width: 350px;
  height: 320px;
  display: flex;
`;

function TripsCard() {
  const [itemData, setItemData] = React.useState([]);

  React.useEffect(() => {
    // console.log(firebase.auth().currentUser.uid);
    firebase
      .firestore()
      .collection("successfullyOrder")
      .where("uid", "==", firebase.auth().currentUser.uid)
      .get()
      .then((snapshot) => {
        console.log(snapshot);
        snapshot.forEach((doc) => {
          const data = doc.data();
          setItemData((itemData) => [...itemData, data]);
          console.log(itemData);
        });
      });
  }, []);

  return (
    <Trips>
      <Title>Trips</Title>

      {itemData.map((data, index) => {
        return (
          <TripsCards key={index}>
            <div className="trips__cards__item__link">
              <figure className="trips__cards__item__pic-wrap">
                {/* <TripsCardsImage data={data} /> */}
                <img
                  className="trips__cards__item__img"
                  alt="d"
                  src={data.imageURL}
                />
              </figure>
              <div className="trips__cards__item__info">
                <h4 className="trips__cards__item__text">{data.name}</h4>
                <h5 className="trips__cards__item__date">
                  {data.startDate.toDate().toLocaleDateString()}-
                  {data.endDate.toDate().toLocaleDateString()}
                </h5>

                <h5 className="trips__cards__item__date">{data.count}位房客</h5>
              </div>
              <PopupButton itemID={data.itemID} />
            </div>
          </TripsCards>
        );
      })}
    </Trips>
  );
}

export default TripsCard;
