import React from "react";

class CountNum extends React.Component {
  state = { count: 0 };

  handleClickAdd = () => {
    this.setState({
      count: this.state.count + 1,
    });
  };

  handleClickReduce = () => {
    this.setState({
      count: this.state.count - 1,
    });
  };

  render() {
    return (
      <div style={{ textAlign: "center" }}>
        <div
          onClick={this.handleClickReduce}
          style={{ backgroundColor: "white", width: "10px", height: "10px" }}
        >
          -
        </div>
        <span>{this.state.count}</span>
        <button onClick={this.handleClickAdd}>+</button>
      </div>
    );
  }
}

export default CountNum;
