import React from "react";
import TripsCardsItems from "./TripsCardsItems";
import "./TripsCard.css";
import p9 from "../imge/img-9.jpeg";
import PopupButton from "./PopupButton";
import firebase from "firebase";
import styled from "styled-components";

const StyleItemsArea = styled.div`
  width: 100%;
  height: fit-content;
`;

const StyleItems = styled.div`
  width: 100px;
  height: 100px;
  background-color: red;
  padding: 20px;
  margin: 20px;
`;

function TripsCard() {
  const [itemData, setItemData] = React.useState([]);
  console.log(itemData);

  React.useEffect(() => {
    console.log(firebase.auth().currentUser.uid);
    firebase
      .firestore()
      .collection("successfullyOrder")
      .where("uid", "==", firebase.auth().currentUser.uid)
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          const orderID = doc.data().orderID;
          firebase
            .firestore()
            .collection("order")
            .doc(orderID)
            .onSnapshot((docSnapshot) => {
              const data = docSnapshot.data();
              setItemData((itemData) => [...itemData, data]);
            });
        });
      });
  }, []);

  // function renderTrips(itemData){
  //     return(

  //     )
  // }

  return (
    <div className="trips-cards">
      <h1>Trips</h1>
      <StyleItemsArea>
        {itemData.map((data, index) => {
          return (
            <StyleItems key={index}>
              <div>{data.count}</div>
            </StyleItems>
          );
        })}
      </StyleItemsArea>

      {/* <div calssname="trips-cards-wrapper">
          <ul className="trips-cards-items">
            <li className="trips__cards__item">
              <div className="trips__cards__item__link">
                <figure className="trips__cards__item__pic-wrap">
                  <img className="trips__cards__item__img" alt="d" src={p9} />
                </figure>
                <div className="trips__cards__item__info">
                  <h5 className="trips__cards__item__date">2012/12/11</h5>
                  <h5 className="trips__cards__item__text">Victor</h5>
                </div>
                <PopupButton />
              </div>
            </li>
          </ul>
        </div> */}
    </div>
  );
}

export default TripsCard;
