import React from "react";
import "./RoomDetail.css";
import { useParams, useRouteMatch, useHistory } from "react-router-dom";

import MapProductDetail from "./MapProductDetail";
import firebase from "firebase";
import styled from "styled-components";
import { BsFillHeartFill } from "react-icons/bs";
import DateRangePickerExample from "./DateRangePickExample";
import CountNumber from "./ CountNumber";
import Swal from "sweetalert2/dist/sweetalert2.js";
import NativeReviewer from "../imge/tent.png";

const Like = styled.button`
  border: none;
  padding-right: 10px;
  padding-left: 10px;
  padding-top: 8px;
  border-radius: 50%;
  font-size: 20px;
  margin-left: 20px;
  cursor: pointer;
`;

const CuntDate = styled.div`
  margin-top: 20px;
`;

const InstantBook = styled.button`
  color: #00bfac;
  background: white;

  /* justify-content: center; */
  position: relative;
  border: 1px solid #00bfac;
  width: 293px;

  border-radius: 5px;
  font-size: 15px;
  font-weight: 700;
  height: 50px;
  margin: 20px 30px;
  cursor: pointer;
  &:hover {
    border: 1px solid #00bfac;
    background: #00bfac;
    color: white;
  }
`;

const RoomsDetailName = styled.div`
  font-size: 30px;
  font-family: "Montserrat", sans-serif;
  font-weight: 500;
  width: 100%;
`;

const RoomsDetailBox = styled.div`
  margin: 50px;
  padding: 10px;
`;

const RoomsDetailImage = styled.img`
  width: 88%;
  height: 450px;
  margin: 5px;
  object-fit: cover;
  border-radius: 7px;
  margin-right: 10px;
`;

const RoomsDetailStars = styled.div`
  color: black;
  padding: 10px 0;
  font-weight: 500;
`;

const RoomsDetailAddress = styled.div`
  color: rgb(151, 145, 145);
  padding: 10px 0;
  margin-top: -2px;
  margin-left: 10px;
`;

const RoomsOwnerName = styled.div`
  margin-top: 10px;
  font-size: 20px;
  font-weight: 500;
  color: gray;
`;

const RoomsOwnerIntroduce = styled.div`
  font-size: 15px;
  width: 90%;
  margin-top: 10px;
`;

const RoomsCheckBox = styled.div`
  margin: 5px 150px 10px 30px;
  border-radius: 12px;
  box-shadow: rgb(0 0 0 / 12%) 0px 6px 16px;
  border-style: solid;
  border: 1px solid rgb(221, 221, 221);
  width: 350px;
  height: 320px;
`;

const RoomsBoxPrice = styled.div`
  font-size: 20px;
  font-weight: 600;
  height: 50px;
`;

const RoomsBoxStarts = styled.div`
  color: black;
  font-weight: 500;
  font-size: 15px;
  margin-top: 4px;
`;

const RoomsBoxFirstRow = styled.div`
  display: flex;
  margin: 20px 20px 0px 20px;
  padding: 10px;
  justify-content: space-between;
`;

const Equipment = styled.div`
  display: flex;
  width: 60%;
  margin-left: 50px;
`;

const EquipmentArea = styled.div`
  border-radius: 7px;
  border: 1px solid rgb(221, 221, 221);
  width: 27%;
  height: 350px;
  margin-right: 20px;
  margin-top: -250px;
`;

const ReviewerImage = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 50%;
`;

const RoomDetailArea = styled.div`
  margin: 30px 0px 10px 100px;
`;

const MapArea = styled.div`
  margin-bottom: 30px;
`;

const EquipmentAreaTitle = styled.div`
  margin-top: 20px;
  font-weight: 800;
  font-size: 20px;
  margin: 10px;
`;

const EquipmentAreaPoint = styled.div`
  margin: 30px 10px;
  color: gray;
`;

const DetailArea = styled.div`
  display: flex;

  margin-top: 40px;
  border-top: solid;
  border-bottom: solid;
  border-width: 1px;
  border-color: rgb(221, 221, 221);
  width: 53%;
`;

function RoomDetail() {
  const { itemID } = useParams();
  const [item, setItem] = React.useState({});
  const [startDate, setStartDate] = React.useState("");
  const [endDate, setEndDate] = React.useState("");
  const [count, setCount] = React.useState(0);
  const [comment, setComment] = React.useState([]);
  const { url } = useRouteMatch();
  const history = useHistory();

  // const [count, setCount] = useState(0);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .doc(itemID)
      .onSnapshot((docSnapshot) => {
        const data = docSnapshot.data();
        setItem(data);
      });
    // .get()
    // .then((docSnapshot) => {
    //   const data = docSnapshot.data();
    //   setItem(data);
    // });
  }, []);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("comment")
      .where("itemID", "==", itemID)
      .get()
      .then((snapshot) => {
        console.log(typeof snapshot);
        snapshot.forEach((doc) => {
          const data = doc.data();
          console.log(data);
          setComment((comment) => [...comment, data]);
        });
      });
  }, []);

  function toggleCollection() {
    const uid = firebase.auth().currentUser.uid;
    if (isCollected) {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
          collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
        });
    } else {
      firebase
        .firestore()
        .collection("product")
        .doc(itemID)
        .update({
          collectionBy: firebase.firestore.FieldValue.arrayUnion(uid),
        });
    }
  }

  const isCollected = item.collectionBy?.includes(
    firebase.auth().currentUser.uid
  );

  function onSubmit() {
    // const documentRef = firebase.firestore().collection("order").doc();
    const orderID = firebase.firestore().collection("order").doc().id;
    if (startDate === "") {
      Swal.fire("請選擇入住時間");
    } else if (endDate === "") {
      Swal.fire("請輸入退房時間");
    } else if (count === 0) {
      Swal.fire("請選擇入住人數");
    } else {
      firebase
        .firestore()
        .collection("order")
        .doc(orderID)
        .set({
          startDate,
          endDate,
          uid: firebase.auth().currentUser.uid,
          id: itemID,
          count,
        })
        .then(() => {
          history.push(`${url}/order/${orderID}`);
        });
    }
  }

  return (
    <div className="rooms__detail__container">
      <div className="rooms__detail__wrapper">
        <div className="rooms__detail__introduce">
          <RoomDetailArea>
            <RoomsDetailBox>
              <RoomsDetailName>{item.name}</RoomsDetailName>
              <div className="rooms__name__info">
                <RoomsDetailStars>
                  ★{item.stars} ({comment.length} comments)
                </RoomsDetailStars>
                <RoomsDetailAddress>⌲{item.address}</RoomsDetailAddress>
                <Like
                  onClick={toggleCollection}
                  style={isCollected ? { color: "red" } : { color: "grey" }}
                >
                  <BsFillHeartFill />
                </Like>
              </div>
              <div className="rooms__detail__carousel">
                <RoomsDetailImage src={item.imageURL} />
              </div>
              <div className="rooms__owner">
                <RoomsOwnerName>Hosted by Victor Chen</RoomsOwnerName>
                <div className="rooms__stick">
                  <div className="rooms__owner__detail">
                    <RoomsOwnerIntroduce>{item.comment}</RoomsOwnerIntroduce>
                  </div>
                  <div className="rooms__detail__check">
                    <RoomsCheckBox>
                      <RoomsBoxFirstRow>
                        <RoomsBoxPrice>NT.{item.price}/晚</RoomsBoxPrice>
                        <RoomsBoxStarts>
                          ★{item.stars} ({comment.length} comments)
                        </RoomsBoxStarts>
                      </RoomsBoxFirstRow>
                      <div className="rooms__box__date">
                        {/* <SubTitle>入住時間</SubTitle> */}
                        <DateRangePickerExample
                          startDate={startDate}
                          setStartDate={setStartDate}
                          endDate={endDate}
                          setEndDate={setEndDate}
                        />
                        {/* <input className="checkin" placeholder="Check in" />
                      <input className="checkout" placeholder="Check out" /> */}
                      </div>
                      {/* <SubTitle>入住人數</SubTitle> */}
                      <CuntDate>
                        <CountNumber count={count} setCount={setCount} />
                      </CuntDate>
                      <InstantBook
                        // history.push(`${url}/order`)
                        onClick={() => onSubmit()}
                      >
                        Instant book
                      </InstantBook>
                    </RoomsCheckBox>
                  </div>
                </div>
              </div>
            </RoomsDetailBox>
            <Equipment>
              <EquipmentArea>
                <EquipmentAreaTitle>Campsite area</EquipmentAreaTitle>
                <EquipmentAreaPoint>Park at listing</EquipmentAreaPoint>
                <EquipmentAreaPoint>Canvas tent</EquipmentAreaPoint>
              </EquipmentArea>
              <EquipmentArea>
                <EquipmentAreaTitle>Essentials</EquipmentAreaTitle>

                <EquipmentAreaPoint>Pets allowed</EquipmentAreaPoint>
                <EquipmentAreaPoint>Campfires allowed</EquipmentAreaPoint>
              </EquipmentArea>
              <EquipmentArea>
                <EquipmentAreaTitle>Amenities</EquipmentAreaTitle>

                <EquipmentAreaPoint>No wifi</EquipmentAreaPoint>
                <EquipmentAreaPoint>Pack out</EquipmentAreaPoint>
                <EquipmentAreaPoint>Showers</EquipmentAreaPoint>
                <EquipmentAreaPoint>Potable water</EquipmentAreaPoint>
              </EquipmentArea>
            </Equipment>
            <DetailArea>
              <div className="details__title">Details</div>
              <div className="details__info">
                <div className="details__points">Check in:After 3PM</div>
                <div className="details__points">Check out:Before 12PM</div>
                <div className="details__points">
                  On arrival: Meet and greet
                </div>
              </div>
            </DetailArea>
            <div className="Activities">
              <div className="activities__title">Activities</div>
              <div className="activities__sub__title">
                Offered on the Host's property or nearby.
              </div>
              <div className="activities__box">
                <div className="activities__points">
                  <div className="activities__points__name">Biking</div>
                </div>
                <div className="activities__points">
                  <div className="activities__points__name">Boating</div>
                </div>
                <div className="activities__points">
                  <div className="activities__points__name">Hiking</div>
                </div>
                <div className="activities__points">
                  <div className="activities__points__name">Padding</div>
                </div>
                <div className="activities__points">
                  <div className="activities__points__name">Swimming</div>
                </div>
              </div>
            </div>
            <div className="Reviews">
              <div className="reviews__title">{comment.length} Reviews</div>
              {comment.map((data, index) => {
                return (
                  <div className="reviews__box" key={index}>
                    {data.author.photoURL ? (
                      <ReviewerImage src={data.author.photoURL} />
                    ) : (
                      <ReviewerImage src={NativeReviewer} />
                    )}

                    <div className="reviewer__info">
                      <div className="reviewer__stars">★{data.rating}</div>
                      <div className="reviewer__name">{data.author.email}</div>
                      <div className="reviewer__comment">{data.review}</div>
                      <div className="reviewer__date">
                        {data.createdAt.toDate().toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </RoomDetailArea>
          <MapArea>
            <MapProductDetail />
          </MapArea>
        </div>
      </div>
    </div>
  );
}

export default RoomDetail;
