import React from "react";
import "./OrderChack.css";
import p9 from "../imge/img-5.jpeg";
import firebase from "firebase";
import { useParams, useHistory } from "react-router-dom";
import styled from "styled-components";

const Input = styled.input`
  display: flex;
  height: 30px;
  width: 120%;
  margin-top: 10px;
`;

function OrderChack() {
  const { orderID } = useParams();
  const { itemID } = useParams();
  const [order, setOrder] = React.useState();
  const [rooms, setRooms] = React.useState();
  const [cardnumber, setCardnumber] = React.useState();
  const [carddate, setCarddate] = React.useState();
  const [cardcode, setCardCode] = React.useState();
  // const [errormessage, setErrorMessage] = React.useState();
  const history = useHistory();

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("order")
      .doc(orderID)
      .onSnapshot((docSnapshot) => {
        const data = docSnapshot.data();
        setOrder(data);
        // console.log(data);
      });
  }, []);

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .doc(itemID)
      .onSnapshot((docSnapshot) => {
        const data = docSnapshot.data();
        setRooms(data);
        console.log(data);
      });
  }, []);

  function onSubmit() {
    const orderSuccessfullyID = firebase
      .firestore()
      .collection("successfullyOrder")
      .doc().id;
    firebase
      .firestore()
      .collection("successfullyOrder")
      .doc(orderSuccessfullyID)
      .set({
        // id: itemID,
        orderID,
        cardnumber,
        carddate,
        cardcode,
        uid: firebase.auth().currentUser.uid,
        imageURL: rooms.imageURL,
        name: rooms.name,
        location: rooms.locationDetail,
      })
      .then(() => {
        window.alert("已成功訂房");
        history.push("/");
      });
    // .catch((error) => {
    //   switch(e.targer.cardnumber){

    //   }
    // });
  }

  return order ? (
    <div className="order">
      <div className="order__list">
        <div className="order__title">確認並付款</div>
        <h2 className="order__sub__title">你的旅程</h2>
        {/* {order.map((order) => randerOrder(order))} */}
        <div className="order__choose">
          <div className="order__date">
            <div className="order__date__date">日期</div>
            <div className="order__date__range">
              {order.startDate.toDate().toLocaleDateString()}-
              {order.endDate.toDate().toLocaleDateString()}
            </div>
            <div className="order__edit">編輯</div>
          </div>
          <div className="order__guests">
            <div className="order__guests_guests">房客人數</div>
            <div className="order__guests__number">{order.count}位房客</div>
            <div className="order__edit">編輯</div>
          </div>
        </div>

        <div className="order__pay">
          <h2 className="order__pay__title">付款方式：</h2>
          <div className="order__pay__creditcard">
            <form>
              <Input
                value={cardnumber}
                placeholder="卡號"
                onChange={(e) => setCardnumber(e.target.value)}
              />
              <Input
                value={carddate}
                placeholder="到期日"
                onChange={(e) => setCarddate(e.target.value)}
              />
              <Input
                value={cardcode}
                placeholder="安全碼"
                onChange={(e) => setCardCode(e.target.value)}
              />
            </form>
          </div>
          <button className="button" onClick={() => onSubmit()}>
            確認並付款
          </button>
          {/* {errormessage && <h6 style={{ color: "black" }}>{errormessage}</h6>} */}
        </div>
      </div>
      <div className="order__room">
        <div className="room__box">
          <img className="room__img" alt="room__img" src={rooms.imageURL}></img>
          <div className="room__info">
            <div className="room__location">⌲{rooms.locationDetail}</div>
            <div className="room__title">{rooms.name}</div>
            <div className="room__stars">
              ★{rooms.stars}
              <span className="visit"> (5 comments)</span>
            </div>
          </div>

          <div className="room__price">
            <h2 className="room__price__title">價格詳情</h2>
            <div className="room__price__night">NT.{rooms.price} x 1晚</div>
          </div>
        </div>
      </div>
    </div>
  ) : (
    <div>loading</div>
  );
}

export default OrderChack;
