import React from "react";
import firebase from "firebase";
import { useHistory } from "react-router-dom";
import "./AddCamp.css";
import MapAutocomplete from "./MapAutocamplete";
import { v4 as uuidv4 } from "uuid";
import Select from "react-select";

function AddCamp() {
  const [name, setName] = React.useState("");
  const [locationDetail, setLocationDetail] = React.useState("");
  const [guests, setGuests] = React.useState("");
  const [stars, setStars] = React.useState("");
  const [price, setPrice] = React.useState("");
  const [comment, setComment] = React.useState("");
  const [topic, setTopic] = React.useState([]);
  const [commentCount, setCommentCount] = React.useState(0);
  const [product, setProduct] = React.useState([]);
  const [image, setImage] = React.useState(null);
  const [address, setAddress] = React.useState("");
  const [placeId, setPlaceId] = React.useState("");
  const [latitude, setLatitude] = React.useState({});
  const history = useHistory();

  const GetAddress = (addressdata) => {
    setAddress(addressdata[0]);
    setPlaceId(addressdata[1]);
    setLatitude(addressdata[2]);
  };

  // const OnFileChange = (e) => {
  //   // Get Files
  //   for (let i = 0; i < e.target.files.length; i++) {
  //     const newFile = e.target.files[i];
  //     const id = uuidv4();
  //     newFile["id"] = id;
  //     setImage((prevState) => [...prevState, newFile]);
  //   }
  // };

  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .get()
      .then((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((doc) => {
          return doc.data();
        });
        setProduct(data);
      });
  }, []);

  function onSubmit() {
    const documentRef = firebase.firestore().collection("product").doc();
    const imageRef = firebase.storage().ref("Product-image/" + documentRef.id);
    const metadata = {
      contentType: image.type,
    };
    imageRef.put(image, metadata).then(() => {
      imageRef.getDownloadURL().then((imageURL) => {
        console.log(commentCount);
        documentRef
          .set({
            name,
            guests,
            stars,
            price,
            imageURL,
            address,
            placeId,
            latitude,
            comment,
            commentCount,
            topic,
            // id: firebase.firestore.collection("product"),
          })
          .then(() => {
            window.alert("成功新增露營地");
            // history.push("/");
          });
      });
    });
  }

  return (
    <div className="rooms__backstage">
      <div className="rooms__backstage__title">上架露營地</div>
      <div className="rooms__backstage__campaing">
        <div>
          <input
            className="rooms__backstage__campaing__input"
            type="file"
            onChange={(e) => setImage(e.target.files[0])}
            // multiple
            // onChange={OnFileChange}
          />

          <input
            className="rooms__backstage__campaing__input"
            placeholder="露營地名稱"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          {/* <input
          className="rooms__backstage__campaing__input"
          placeholder="露營地地點"
          value={locationDetail}
          onChange={(e) => setLocationDetail(e.target.value)}
        /> */}
          <MapAutocomplete placeaddress={GetAddress} />
          <input
            className="rooms__backstage__campaing__input"
            placeholder="露營地人數"
            value={guests}
            onChange={(e) => setGuests(e.target.value)}
          />
          <input
            className="rooms__backstage__campaing__input"
            placeholder="露營地評分"
            value={stars}
            onChange={(e) => setStars(e.target.value)}
          />
          <input
            className="rooms__backstage__campaing__input"
            placeholder="露營地價格"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <input
            className="rooms__backstage__campaing__input"
            placeholder="露營地介紹"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
        </div>
        <div className="rooms__infor">
          其他露營資訊
          <form>
            露營主題：
            <Select
              className="select"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              option={options}
            >
              {/* <option value="懶人露營">懶人露營</option>
              <option value="海景">老師B</option>
              <option value="路人C">路人C</option>
              <option value="寵物D">寵物D</option> */}
            </Select>
          </form>
          {/* <form>
            優惠資訊：
            <select
              className="select"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
            >
              <option value="懶人露營">懶人露營</option>
              <option value="海景">老師B</option>
              <option value="路人C">路人C</option>
              <option value="寵物D">寵物D</option>
            </select>
          </form>
          <form>
            優惠資訊：
            <select
              className="select"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
            >
              <option value="懶人露營">懶人露營</option>
              <option value="海景">老師B</option>
              <option value="路人C">路人C</option>
              <option value="寵物D">寵物D</option>
            </select>
          </form> */}
        </div>
      </div>
      <button className="send__btn" onClick={() => onSubmit()}>
        送出
      </button>
    </div>
  );
}

export default AddCamp;
