import React from "react";
import Popup from "reactjs-popup";
import "./PopupButtonSignUp.css";
import Logo from "../imge/logo.png";
import firebase from "../utils/firebase";
import { useHistory } from "react-router-dom";
import styled from "styled-components";

const NativeSignupBtn = styled.div`
  background: #00bfac;
  font-size: 18px;
  color: white;
  padding: 12px 20px;
  border: 1px gray;
  border-radius: 3px;
  text-align: center;
  cursor: pointer;
  margin-top: 10px;
  font-weight: bold;
`;

const Modal = styled.div`
  font-size: 23px;
`;

const SignupTitle = styled.div`
  width: 100%;
  font-size: 30px;
  text-align: center;
  padding: 5px;
  font-weight: bold;
  margin-bottom: 15px;
`;

const SignupSubTitle = styled.div`
  width: 100%;
  font-size: 20px;
  text-align: center;
  padding: 5px;
  color: gray;
`;

const SignupAreas = styled.div`
  width: 100%;
  padding: 10px 5px;
`;

function PopupButtonSignUp() {
  const history = useHistory();
  const [email, setEmail] = React.useState("");
  const [password, setPsaaword] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  function onSubmit() {
    firebase
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => {
        history.push("/");
      })
      .catch((error) => {
        console.log(error);
        switch (error.code) {
          case "auth/email-already-in-use":
            setErrorMessage(" This Email has already been registered");
            break;
          default:
        }
        switch (error.code) {
          case "auth/invalid-email":
            setErrorMessage(" Incorrect Email format");
            break;
          default:
        }
        switch (error.code) {
          case "auth/weak-password":
            setErrorMessage(" Insufficient password strength");
            break;
          default:
        }
      });
  }

  return (
    <Popup trigger={<NativeSignupBtn>Sign up</NativeSignupBtn>} modal nested>
      {(close) => (
        <Modal>
          <button className="close" onClick={close}>
            X
          </button>
          <SignupTitle>
            Welcome to{" "}
            <img className="signup__logo" src={Logo} alt="signup__logo" />
          </SignupTitle>
          <SignupSubTitle>
            Search, discover and book
            <br />
            Everywhere you want to camp.
          </SignupSubTitle>
          <SignupAreas>
            {" "}
            <form>
              <div
                type="button"
                name="btn-google-login"
                className="btn-google-login"
              >
                Google login
              </div>
              <div
                type="button"
                name="btn-facebook-login"
                className="btn-facebook-login"
              >
                Facebook login
              </div>
              <form>
                <div className="signup-name">
                  <input
                    className="signup-input"
                    name="name"
                    type="name"
                    placeholder="name"
                  ></input>
                </div>
                <div className="signup-email">
                  <input
                    className="signup-input"
                    name="email"
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  ></input>
                </div>
                <div className="signup-password">
                  <input
                    className="signup-input"
                    naem="password"
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPsaaword(e.target.value)}
                  ></input>
                </div>
                {errorMessage && (
                  <h6 style={{ color: "red" }}>{errorMessage}</h6>
                )}
                <div
                  type="button"
                  name="btn-Native-signup"
                  className="btn-nativ-signup"
                  onClick={() => {
                    setErrorMessage("");
                    onSubmit();
                  }}
                >
                  Sign up
                </div>
              </form>
            </form>
          </SignupAreas>
        </Modal>
      )}
    </Popup>
  );
}

export default PopupButtonSignUp;
