import React from "react";
import "./TripsCard.css";
import PopupButton from "./PopupButton";
import firebase from "firebase";
import styled from "styled-components";

const Trips = styled.div`
  margin: 0px auto;
  padding: 30px;
  width: 50%;
`;

const Title = styled.div`
  font-size: 40px;
  text-align: center;
  margin: 20px;
  padding: 10px;
  font-weight: 600;
`;

const TripsCards = styled.div`
  margin: 5px 150px 10px 30px;
  border-radius: 12px;
  box-shadow: rgb(0 0 0 / 12%) 0px 6px 16px;
  border-style: solid;
  border: 1px solid rgb(221, 221, 221);
  width: 350px;
  height: 320px;
`;

const Image = styled.img`
  width: 100%;
  height: 70%;
  overflow: hidden;
  border-radius: 7px;
`;

const TripsCardsBox = styled.div`
  padding: 0px 10px 10px 20px;
`;

const CardName = styled.div`
  color: #252e48;
  font-size: 18px;
  font-weight: 800;
  line-height: 5px;
  margin-top: 15px;
`;

const CardDate = styled.div`
  color: gray;
  font-weight: 600;
  font-size: 15px;
  margin-top: 15px;
`;

const CardCount = styled.div`
  font-size: 5px;
  font-weight: 700;
  margin-top: 5px;
`;

function TripsCard() {
  const [itemData, setItemData] = React.useState([]);

  React.useEffect(() => {
    // console.log(firebase.auth().currentUser.uid);
    firebase
      .firestore()
      .collection("successfullyOrder")
      .where("uid", "==", firebase.auth().currentUser.uid)
      .get()
      .then((snapshot) => {
        console.log(snapshot);
        snapshot.forEach((doc) => {
          const data = doc.data();
          setItemData((itemData) => [...itemData, data]);
          console.log(itemData);
        });
      });
  }, []);

  return (
    <Trips>
      <Title>Trips</Title>

      {itemData.map((data, index) => {
        return (
          <TripsCards>
            {/* <TripsCardsImage data={data} /> */}
            <Image src={data.imageURL} />

            <TripsCardsBox>
              <CardName>{data.name}</CardName>
              <CardDate>
                {data.startDate.toDate().toLocaleDateString()}-
                {data.endDate.toDate().toLocaleDateString()}
              </CardDate>

              <CardCount>{data.count}位房客</CardCount>
            </TripsCardsBox>
            <PopupButton itemID={data.itemID} />
          </TripsCards>
        );
      })}
    </Trips>
  );
}

export default TripsCard;
