import React from "react";
import firebase from "firebase";
import { useHistory } from "react-router-dom";
function Backstage() {
  const [name, setName] = React.useState("");
  const [location, setLocation] = React.useState("");
  const [guests, setGuests] = React.useState("");
  const [stars, setStars] = React.useState("");
  const [price, setPrice] = React.useState("");
  const [product, setProduct] = React.useState([]);
  const [files, setFile] = React.useState(null);
  const history = useHistory();

  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .get()
      .then((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((doc) => {
          return doc.data();
        });
        setProduct(data);
      });
  }, []);

  function onSubmit() {
    const documentRef = firebase.firestore().collection("product").doc();
    documentRef
      .set({
        name,
        location,
        guests,
        stars,
        price,
      })
      .then(() => {
        history.push("/");
      });
  }

  return (
    <div className="rooms__backstage">
      上架露營地
      <div className="rooms__backstage__campaing">
        {/* <img
          className="rooms__backstage__back__img"
          src="https://react.semant-ui.com/images/wireframe/image.png"
          alt="back__img"
        />  */}

        <input
          className="rooms__backstage__campaing__img"
          type="file"
          accept="image/png"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <input
          className="rooms__backstage__campaing__name"
          placeholder="露營地名稱"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="rooms__backstage__campaing__location"
          placeholder="露營地地點"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <input
          className="rooms__backstage__campaing__guests"
          placeholder="露營地人數"
          value={guests}
          onChange={(e) => setGuests(e.target.value)}
        />
        <input
          className="rooms__backstage__campaing__stars"
          placeholder="露營地評分"
          value={stars}
          onChange={(e) => setStars(e.target.value)}
        />
        <input
          className="rooms__backstage__campaing__price"
          placeholder="露營地價格"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <button className="send__btn" onClick={() => onSubmit()}>
          送出
        </button>
      </div>
    </div>
  );
}

export default Backstage;
