import React, { useState, useRef } from "react";
import "../App.css";
import video from "../video/video-3.mp4";
import DateRangePickerExample from "./DateRangePickExample";
import CountNum from "./CountNum";
import styled from "styled-components";
// import { useHistory } from "react-router-dom";

const Video = styled.video`
  object-fit: cover;
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: -1;
`;

const Container = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: inset 0 0 0 1000px rgba(0, 0, 0, 0.2);
  object-fit: contain;
`;

const Title = styled.h1`
  color: #fff;
  font-size: 100px;
  margin-top: -100px;
  @media screen and (max-width: 960px) {
    font-size: 70px;
    margin-top: -150px;
  }
  @media screen and (max-width: 768px) {
    font-size: 50px;
    margin-top: -100px;
  }
`;

const SubTitle = styled.p`
  margin-top: 8px;
  color: #fff;
  font-size: 32px;
  font-family: "Trebuchet MS", "Lucida Sans Unicode", "Lucida Grande",
    "Lucida Sans", Arial, sans-serif;
  @media screen and (max-width: 768px) {
    font-size: 30px;
  }
`;

const SearchTitle = styled.h5`
  color: #fff;
`;

const SearchInput = styled.input`
  margin-top: -10px;
  background-color: whitesmoke;
  padding: 8px 20px;
  border-radius: 13px;
  margin-right: 10px;
  outline: none;
  border: none;
  font-size: 18px;
  margin-bottom: 16px;
  border: 1px solid #eae8e4;
  width: 300px;
  display: flex;
  @media screen and (max-width: 960px) {
    width: 300px;
    display: flex;
  }
`;

const BtnSearch = styled.div`
  background-color: #ffb517;
  color: #fff;
  padding: 8px 20px;
  border: 1px;
  transition: all 0.3s ease-out;
  text-align: center;
  cursor: pointer;
  border-radius: 5px;
  font-weight: bold;
  margin-top: 24px;
`;

function HeroSection() {
  // const [search, setSearch] = useState("");
  // const searchValue = useRef(null);
  // const history = useHistory();

  // const clickSearch = () => {
  //   console.log(searchValue.current.value);
  //   history.push(`/${searchValue.current.value}`);
  // };

  return (
    <Container>
      <Video src={video} autoPlay loop muted></Video>
      <Title>START CAMPING</Title>
      <SubTitle>Find your best campground</SubTitle>

      <div className="search-areas">
        <form>
          <div className="search-location">
            <SearchTitle>WHERE TO?</SearchTitle>
            <SearchInput
              name="location"
              type="location"
              placeholder="Try somewhere"
              // ref={searchValue}
            />
          </div>

          <SearchTitle>DATES</SearchTitle>
          <DateRangePickerExample />

          <div className="search-guests">
            <SearchTitle>GUESTS</SearchTitle>
            <CountNum />
          </div>
          <BtnSearch
            type="button"
            name="search-button"
            // onClick={() => clickSearch()}
          >
            Search
          </BtnSearch>
        </form>
      </div>
    </Container>
  );
}

export default HeroSection;
