  //         createdAt: firebase.firestore.Timestamp.now(),
  //         rating,
  //         author: {
  //           photoURL: firebase.auth().currentUser.photoURL || "",
  //           email: firebase.auth().currentUser.email,
  //           // name: firebase.auth().currentUser.name,
  //         },
  //       })
  //       .then(() => {
  //         firebase
  //           .firestore()
  //           .collection("product")
  //           .doc(props.itemID)
  //           .get()
  //           .then((doc) => doc.data())
  //           .then((res) => {
  //             const data = { commentCount: res.commentCount + 1 };
  //             firebase
  //               .firestore()
  //               .collection("product")
  //               .doc(props.itemID)
  //               .update(data);
  //           })
  //           .then(() => {
  //             Swal.fire("已成功完成評價");
  //             history.push("/");
  //           });
  //       });
  //   }

  return (
    <Popup
      trigger={<TripsCardEvaluate> 查看評論 </TripsCardEvaluate>}
      modal
      nested
    >
      {(close) => (
        <div className="modal">
          <button className="close" onClick={close}>
            &times;
          </button>
          <div className="header"> 查看評論 </div>
          <div className="content">
            {" "}
            <div className="content-input">
              Stars:
              <StarRating
                setSelection={setSelection}
                setRating={setRating}
                selection={selection}
                rating={parseInt(comment.rating, 10)}
              />
            </div>
            <br />
            <div className="content-input">
              Reviews:
              {comment.review}
            </div>
          </div>
          <div className="actions"></div>
        </div>
      )}
    </Popup>
  );
}

export default PopupIsCommented;
