import React from "react";

import "./Rooms.css";
import { Link } from "react-router-dom";
import firebase from "firebase";
import styled from "styled-components";

const Item = styled.div`
  display: flex;
  border-bottom: 1px solid rgb(212, 212, 212);
  margin-bottom: 8px;
  padding: 10px;
  width: 100%;
`;

function MyWishList() {
  const [rooms, setRooms] = React.useState([]);
  React.useEffect(() => {
    firebase
      .firestore()
      .collection("product")
      .where("collectionBy", "array-contains", firebase.auth().currentUser.uid)
      .onSnapshot((collectionSnapshot) => {
        const data = collectionSnapshot.docs.map((docSnapshot) => {
          const id = docSnapshot.id;
          return { ...docSnapshot.data(), id };
        });
        setRooms(data);
        console.log(data);
      });
  }, []);

  function renderRoom(item) {
    return (
      <Item>
        <Link className="rooms_item__link" to={`/products/${item.id}`}>
          <img className="rooms__item__img" alt="room" src={item.imageURL} />
        </Link>
        <div className="box">
          <div className="rooms__item__location">⌲{item.location}</div>
          <div className="rooms__item__title">{item.name}</div>
          <hr />
          <div className="rooms_item__guest">{item.guests} guests</div>
          <div className="rooms__item__stars">
            ★{item.stars}
            <span className="visits"> (5 comments)</span>
          </div>
          <div className="rooms__item__price">{item.price}/night</div>
          <div className="rooms__item__heart"></div>
        </div>
      </Item>
    );
  }

  return (
    <div className="rooms">
      <div className="rooms__container">
        <div className="rooms__wrapper">
          <div className="rooms__items">
            <div className="title">Wishlists</div>
            {rooms.map((item) => renderRoom(item))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MyWishList;
