import React from "react";

import video from "../video/video-4.mp4";
import "./HeroSectionLogin.css";
import PopupButtonSignUp from "./PopupButtonSignUp";
import firebase from "firebase";
import { useHistory } from "react-router";

function HeroSectionLogin() {
  const history = useHistory();
  const [LoginEmail, setLoginEmail] = React.useState("");
  const [LoginPassword, setLoginPsaaword] = React.useState("");
  const [errorMessage, setErrorMessage] = React.useState("");

  var fbProvider = new firebase.auth.FacebookAuthProvider();
  const fbLogin = () => {
    firebase
      .auth()
      .signInWithPopup(fbProvider)
      .then((user) => {
        console.log(user);
        // createUserDoc(user, userName);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  function onSubmit() {
    firebase
      .auth()
      .signInWithEmailAndPassword(LoginEmail, LoginPassword)
      .then(() => {
        history.push("/");
      })
      .catch((error) => {
        console.log(error);
        switch (error.code) {
          case "auth/invalid-email":
            setErrorMessage(" Incorrect Email format");
            break;
          default:
        }

        switch (error.code) {
          case "auth/wrong-password":
            setErrorMessage(" Wrong password");
            break;
          default:
        }
      });
  }

  return (
    <div className="log-in-hero-container">
      <video src={video} autoPlay loop muted></video>
      <h1>Welcome back!</h1>
      <p>Let's get you intside.</p>
      <div className="login-areas">
        <form>
          <div
            type="button"
            name="btn-google-login"
            className="btn-google-login"
          >
            Google log in
          </div>
          <div
            type="button"
            name="btn-facebook-login"
            className="btn-facebook-login"
            onClick={fbLogin}
          >
            Facebook log in
          </div>
          <form>
            <div className="login-email">
              <input
                className="login-input"
                name="email"
                type="email"
                placeholder="Email"
                value={LoginEmail}
                onChange={(e) => setLoginEmail(e.target.value)}
              ></input>
            </div>
            <div className="login-password">
              <input
                className="login-input"
                naem="password"
                type="password"
                placeholder="Password"
                value={LoginPassword}
                onChange={(e) => setLoginPsaaword(e.target.value)}
              ></input>
            </div>
            {errorMessage && <h6 style={{ color: "white" }}>{errorMessage}</h6>}
            <div
              type="button"
              name="btn-Native-login"
              className="btn-nativ-login"
              onClick={() => {
                setErrorMessage("");
                onSubmit();
              }}
            >
              Log in
            </div>
          </form>
          <PopupButtonSignUp />
        </form>
      </div>
    </div>
  );
}

export default HeroSectionLogin;
