  //               .doc(props.itemID)
  //               .update(data);
  //           })
  //           .then(() => {
  //             Swal.fire("已成功完成評價");
  //             history.push("/");
  //           });
  //       });
  //   }

  function onCheck() {
    firebase
      .firestore()
      .collection("comment")
      .where("uid", "==", firebase.auth().currentUser.uid)
      .where("itemID", "==", props.itemID)
      .get()
      .then((onSnapshot) => {
        const data = onSnapshot.data();
        setComment(data);
        console.log(data);
      });
  }

  return (
    <Popup
      trigger={
        <TripsCardEvaluate onClick={() => onCheck()}>
          {" "}
          查看評論{" "}
        </TripsCardEvaluate>
      }
      modal
      nested
    >
      {(close) => (
        <div className="modal">
          <button className="close" onClick={close}>
            &times;
          </button>
          <div className="header"> 查看評論 </div>
          <div className="content">
            {" "}
            <div className="content-input">
              Stars:
              <StarRating
              // setSelection={setSelection}
              // setRating={setRating}
              // selection={selection}
              // rating={rating}
              />
            </div>
            <br />
            <div className="content-input">
              Reviews:
              <input
                className="popupInput"
                value={review}
                onChange={(e) => setReview(e.target.value)}
              ></input>
            </div>
          </div>
          <div className="actions"></div>
        </div>
      )}
    </Popup>
  );
}

export default PopupIsCommented;
