import React from "react";
import GoogleMap from "./GoogleMap";
import "./Rooms.css";
import { Link } from "react-router-dom";
import firebase from "firebase";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { BsFillHeartFill } from "react-icons/bs";
import algolia from "../utils/algolia";
import GoogleMapSearch from "./GoogleMapSearch";

const Like = styled.button`
  border: none;
  padding-right: 10px;
  padding-left: 10px;
  padding-top: 8px;
  border-radius: 50%;
  font-size: 20px;
  /* margin-left: -20px; */
  cursor: pointer;
`;

const SearchResult = styled.div`
  font-size: 30px;
  font-weight: 700;
`;

function SearchRoom() {
  const [rooms, setRooms] = React.useState();
  const [searchResults, setSearchResults] = React.useState([]);
  const search = useParams();

  React.useEffect(() => {
    algolia.search(search.inputValue).then((result) => {
      console.log(result);
      const searchResults = result.hits.map((hit) => {
        // return {
        //   //   name: hit.name,
        //   //   address: hit.address,
        //   //   comment: hit.comment,
        //   //   commentCount: hit.commentCount,
        //   //   guests: hit.guests,
        //   //   imageURL: hit.imageURL,
        //   //   latitude: hit.latitude,
        //   //   price: hit.price,
        //   //   stars: hit.stars,
        //   objectID: hit.objectID,
        //   //   collectionBy: hit.collectionBy,
        // };
        return hit.objectID;
      });
      setSearchResults(searchResults);
      console.log(searchResults);
    });
  }, [search.inputValue]);

  React.useEffect(() => {
    if (searchResults.length > 0) {
      firebase
        .firestore()
        .collection("product")
        .where("__name__", "in", searchResults)
        .onSnapshot((collectionSnapshot) => {
          const data = collectionSnapshot.docs.map((docSnapshot) => {
            const id = docSnapshot.id;
            return { ...docSnapshot.data(), id };
          });
          setRooms(data);
          console.log(data);
        });
    }
  }, [searchResults]);

  function renderRoom(item) {
    const uid = firebase.auth().currentUser.uid;
    function toggleCollection() {
      if (isCollected) {
        firebase
          .firestore()
          .collection("product")
          .doc(item.id)
          .update({
            collectionBy: firebase.firestore.FieldValue.arrayRemove(uid),
          });
      } else {
        firebase
          .firestore()
          .collection("product")
          .doc(item.id)
          .update({
            collectionBy: firebase.firestore.FieldValue.arrayUnion(uid),
          });
      }
    }

    const isCollected = item.collectionBy?.includes(
      firebase.auth().currentUser.uid
    );

    return (
      <div className="rooms__item">
        <Link className="rooms_item__link" to={`/products/${item.id}`}>
          <img className="rooms__item__img" alt="room" src={item.imageURL} />
        </Link>
        <div className="box">
          <div className="rooms__item__location">⌲{item.address}</div>
          <div className="rooms__item__title">{item.name}</div>
          <hr />
          <div className="rooms_item__guest">{item.guests} guests</div>
          <div className="rooms__item__stars">
            ★{item.stars}
            <span className="visits"> ({item.commentCount} comments)</span>
          </div>
          <Like
            onClick={toggleCollection}
            style={isCollected ? { color: "red" } : { color: "grey" }}
          >
            <BsFillHeartFill />
          </Like>
          {/* <div className="rooms__item__like">like:{props.like}</div> */}
          <div className="rooms__item__price">{item.price}/night</div>
          <div className="rooms__item__heart"></div>
        </div>
      </div>
    );
  }

  return rooms ? (
    <div className="rooms">
      {}
      <div className="rooms__condition__container">
        <div className="rooms__condition__wrapper">
          <ul className="rooms__condition__items">
            <SearchResult>Search Result"{search.inputValue}"</SearchResult>
            {/* <button className="btn__condition">Seaview</button>
            <button className="btn__condition">Mountainview</button>
            <button className="btn__condition">Swimming Pool</button>
            <button className="btn__condition">Barbecue</button> */}
          </ul>
        </div>
      </div>
      <div className="rooms__container">
        <div className="rooms__wrapper">
          <div className="rooms__items">
            {/* {rooms.filter(room => tagstate ? room.status === tagstate || true) */}
            {/* {searchResults.map((item) => renderRoom(item))} */}
            {rooms.map((item) => renderRoom(item))}
          </div>
          <div className="map__wrapper">
            <div className="map">
              <GoogleMapSearch search={rooms} />
            </div>
          </div>
        </div>
      </div>
    </div>
  ) : (
    <div>No relevant information</div>
  );
}

export default SearchRoom;
