import React from "react";
import { Link } from "react-router-dom";
import "./Rooms.css";

function RoomsItem(props) {
  return (
    <>
      <div className="rooms__item">
        <Link className="rooms_item__link" to="/products/detail">
          <img className="rooms__item__img" alt="room" src={props.src} />
        </Link>
        <div className="box">
          <div className="rooms__item__location">⌲{props.location}</div>
          <div className="rooms__item__title">{props.name}</div>
          <hr />
          <div className="rooms_item__guest">{props.totalGuest} guests</div>
          <div className="rooms__item__stars">
            ★{props.rating}
            <span className="visits"> ({props.numberOfRating} comments)</span>
          </div>
          <button className="btn__like">♥︎ Add to wishlist</button>
          {/* <div className="rooms__item__like">like:{props.like}</div> */}
          <div className="rooms__item__price">{props.price}/night</div>
          <div className="rooms__item__heart"></div>
        </div>
      </div>
    </>
  );
}

export default RoomsItem;
