import React from "react";

class CountNum extends React.Component {
  state = { count: 0 };

  handleClickAdd = () => {
    this.setState({
      count: this.state.count + 1,
    });
  };

  handleClickReduce = () => {
    this.setState({
      count: this.state.count - 1,
    });
  };

  render() {
    return (
      <div>
        <div
          onClick={this.handleClickReduce}
          style={{
            backgroundColor: "white",
            // width: "20px",
            // height: "20px",
            // cursor: "pointer",
            // textAlign: "center",
          }}
        >
          -
        </div>
        <span>{this.state.count}</span>
        <div
          onClick={this.handleClickAdd}
          style={{
            backgroundColor: "white",
            // width: "20px",
            // height: "20px",
            // cursor: "pointer",
            // textAlign: "center",
          }}
        >
          +
        </div>
      </div>
    );
  }
}

export default CountNum;
